package ayds.dictionary.foxtrot.model;public class Definition {    private String term;    private String meaning;    public Definition(String term, String meaning){        this.term = term;        this.meaning = meaning;    }    public String getTerm(){        return term;    }    public String getMeaning() {        return meaning;    }    public void setMeaning( String meaning){ this.meaning= meaning;}    public boolean isMeaningEmpty(){        return (getMeaning()==null || getMeaning().length()==0);    }}