package ayds.dictionary.foxtrot.model;public class Definition {    private String term;    private String meaning;    private Source source;    public Definition(String term, String meaning, Source source){        this.term = term;        this.meaning = meaning;        this.source = source;    }    public String getTerm(){        return term;    }    public String getMeaning() {        return meaning;    }    public void setMeaning( String meaning){ this.meaning= meaning;}    public boolean isMeaningEmpty(){        return (getMeaning()==null || getMeaning().length()==0);    }    public Source getSource(){        return source;    }}