package ayds.dictionary.foxtrot.model.externalServices.adapters;import ayds.dictionary.delta.services.BigHugeLabsModule;import ayds.dictionary.foxtrot.services.YandexServiceModule;import wikipedia.service.WikipediaServiceModule;public class ServiceMapFactoryModule {    private static ServiceMapFactoryModule instance;    private ServicesMapFactory servicesMapFactory;    public static ServiceMapFactoryModule getInstance() {        if (instance== null) {            instance = new ServiceMapFactoryModule();        }        return instance;    }    private ServiceMapFactoryModule() {        servicesMapFactory= new ServiceMapFactoryImpl(YandexServiceModule.getInstance().getRemoteSource(),                                                      WikipediaServiceModule.getInstance().getService(),                                                       BigHugeLabsModule.getInstance().getBigHugeLabsService()        );    }    public ServicesMapFactory getServicesMapFactory(){       return  servicesMapFactory;    }}